---- This sample demonstrates usage of transforms with-- push/pop functionality---- Result:--     Checkboard 3x3 pattern with one more black block --     on lover right cornerfunction draw()    -- Push, Pop and Reset    translate(100, 100)    rect(0, 0, 100, 100)    pushTransform()    resetTransform()    rect(0, 0, 100, 100)    popTransform()    rect(100, 100, 100, 100)    translate(100, 100)    rect(100, 100, 100, 100)    -- Test save    saveTransform("test")    -- Check if we are at 0    resetTransform()    rect(200, 0, 100, 100)    -- Test load    loadTransform("test")    translate(0, 200)    rect(0, 0, 100, 100)end