x = 0y = 0-- We devide numbers by 100 so that we get values in range of 0.1 to 0.5, -- in this way the object doesn't move too fast nor too slowvx = math.random(10, 50) / 100vy = math.random(10, 50) / 100function Created()endfunction Started()endfunction Draw()    -- Set styles    background(0, 0, 0)    fill(1, 1, 1)    -- Calculate deltas    local deltaX = vx * DELTA    local deltaY = vy * DELTA    -- Update X    if x + deltaX >= WIDTH - 50 or x + deltaX <= 0 then         vx = -vx     else         x = x + deltaX    end    -- Update Y    if y + deltaY >= HEIGHT - 50 or y + deltaY <= 0 then         vy = -vy    else         y = y + deltaY    end    -- Draw rectangle    rect(x, y, 50, 50)endfunction Touched(type, x, y)endfunction Stopped()end